```python
def is_prime(n):
    """Check if a number is prime."""
    if n <= 1:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    for i in range(3, int(n**0.5) + 1, 2):
        if n % i == 0:
            return False
    return True

def sum_of_digits(n):
    """Calculate the sum of the digits of a number."""
    return sum(int(digit) for digit in str(n))

# List to store the two-digit primes whose sum of digits is 8
two_digit_primes_with_sum_8 = []

# Iterate through all two-digit numbers
for num in range(10, 100):
    if is_prime(num) and sum_of_digits(num) == 8:
        two_digit_primes_with_sum_8.append(num)

# The number of such primes
number_of_primes = len(two_digit_primes_with_sum_8)
print((number_of_primes, two_digit_primes_with_sum_8))
